    public List<String> getOrdersByCustomer(String customerName) {
        List<String> customerOrders = new ArrayList<>();
        for (String order : orders) {
            if (order.contains(customerName)) {
                customerOrders.add(order);
            }
        }
        return customerOrders;
    }

